DROP TABLE IF EXISTS `course`;
CREATE TABLE `course` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `name` varchar(200) NOT NULL COMMENT '名称',
  `description` varchar(200) DEFAULT NULL COMMENT '描述',
  `course_category_id` bigint(20) NOT NULL COMMENT '分类',
  `course_type` int(2) NOT NULL DEFAULT '0' COMMENT '课程类型',
  `course_key` varchar(100) NOT NULL COMMENT '课程本地标示',
  `cover_image_link` varchar(200) NOT NULL COMMENT '封面图片链接',
  `can_match_duty_rank` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否匹配职务级别',
  `duty_rank` varchar(100) DEFAULT NULL COMMENT '职务级别',
  `can_match_trade` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否匹配行业',
  `trade` varchar(100) DEFAULT NULL COMMENT '行业',
  `class_hour` int(6) NOT NULL DEFAULT '0' COMMENT '学时',
  `can_allow_all_user` bit(1) NOT NULL DEFAULT b'1' COMMENT '是否允许所有学员学习',
  `user_joined_data` text COMMENT '学员参与范围',
  `joined_organs` text COMMENT '参加课程的单位列表',
  `study_num` int(8) NOT NULL DEFAULT '0' COMMENT '学习次数',
  `credit` int(5) NOT NULL DEFAULT '0' COMMENT '学分',
  `pass_credit_limit` int(5) NOT NULL DEFAULT '0' COMMENT '通过所需的最低学分',
  `status` int(2) NOT NULL DEFAULT '0' COMMENT '状态',
  `created_time` datetime NOT NULL COMMENT '创建时间',
  `creator` bigint(20) NOT NULL COMMENT '创建人',
  PRIMARY KEY (`id`),
  KEY `fk_course_1` (`course_category_id`),
  CONSTRAINT `fk_course_1` FOREIGN KEY (`course_category_id`) REFERENCES `course_category` (`id`) ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='课程表';

