DROP TABLE IF EXISTS `organ`;
CREATE TABLE `organ` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `name` varchar(200) NOT NULL COMMENT '帐号',
  `password` varchar(200) NOT NULL COMMENT '密码',
  `parent_id` bigint(20) NOT NULL DEFAULT '0' COMMENT '上级单位编号',
  `organ_type` int(2) NOT NULL DEFAULT '0' COMMENT '单位类型',
