-- ----------------------------
-- Function structure for `getOrganChildIdList`
-- ----------------------------
DROP FUNCTION IF EXISTS `getOrganChildIdList`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `getOrganChildIdList`(rootId BIGINT) RETURNS varchar(2000) CHARSET utf8
BEGIN
       DECLARE sTemp VARCHAR(2000);
       DECLARE sTempChd VARCHAR(2000);
    
       SET sTemp = '$';
       SET sTempChd =cast(rootId as CHAR);
    
       WHILE sTempChd is not null DO
         SET sTemp = concat(sTemp,',',sTempChd);
         SELECT group_concat(id) INTO sTempChd FROM organ where FIND_IN_SET(parent_id ,sTempChd)>0;
       END WHILE;
       RETURN sTemp;
     END
;;
DELIMITER ;
